<style lang="scss">
@import "../../scss/_global.scss";

#delivery {
  @extend %pbt;

  .title {
    gap: 30px;

    @media (max-width: 1024px) {
      gap: 20px;
    }

    @media (max-width: 576px) {
      gap: 10px;
    }
  }

  .delivery-wrapper {
    display: grid;
    grid-template-columns: 65% 1fr;
    gap: 30px;

    @media (max-width: 1440px) {
      gap: 20px;
      align-items: center;
    }

    @media (max-width: 1366px) {
      // grid-template-columns: 1fr;
      gap: 40px;
      grid-template-columns: auto auto;
      max-width: max-content;
      margin: 0 auto;
    }
    @media (max-width: 992px) {
      gap: 40px;
      grid-template-columns: auto;

      max-width: 410px;
      width: 100%;
      margin: 0 auto;

      form {
        width: 100%;
      }
    }

    @media (max-width: 425px) {
      gap: 18px;
    }

    @media (max-width: 768px) {
      max-width: 360px;
    }

    &__form {
      display: flex;
      align-items: center;
      gap: 30px;
      justify-content: space-evenly;

      @media (min-width: 1367px) {
        box-shadow: 5px 5px 30px 0px rgba(0, 0, 0, 0.15);
      }

      @media (min-width: 993px) {
        background: #fff;
        border-radius: 20px;

        padding: 20px;
      }
      .form-img {
        @media (max-width: 1366px) {
          display: none;
        }
      }
    }

    &__features {
      @media (max-width: 992px) {
        display: none;
      }

      .features-item {
        display: grid;
        grid-template-columns: 25% 1fr;
        flex-direction: column;
        gap: 26px;

        &:not(:last-child) {
          margin-bottom: 30px;

          // @media (max-width: 1366px) {
          //   margin-bottom: 20px;
          // }
        }

        &__svg {
          // @media (max-width: 768px) {
          //   flex-shrink: 0;
          // }
          svg {
            display: block;
            margin: 0 auto;
            // margin-bottom: 30px;

            // @media (max-width: 1280px) {
            //   margin-bottom: 25px;
            // }
          }
        }

        &__content {
          h4 {
            margin-bottom: 5px;
            white-space: nowrap;

            color: #323232;
            font-size: 24px;
            font-family: "Lato";
            font-style: normal;
            font-weight: 600;
            line-height: 120%;

            br {
              display: none;
            }
          }

          p {
            white-space: nowrap;

